 ('512', '338', '3', '109', 'en_us', '82', '', '{\"typeId\":\"3\",\"authorId\":\"109\",\"title\":\"Shuttel Identity\",\"slug\":\"shuttel-identity\",\"postDate\":1460472420,\"expiryDate\":null,\"enabled\":1,\"parentId\":null,\"fields\":{\"3\":{\"344\":{\"type\":\"textFullWidth\",\"enabled\":\"1\",\"fields\":{\"body\":\"Shuttel is a brand new service that combines many different ways of traveling through The Netherlands. With one card you have access to bicycles, rental cars, trams, trains and more. We were asked to create their brand guidelines with production ready assets.\",\"textColor\":\"#000000\",\"backgroundColor\":\"#ffffff\",\"backgroundImage\":\"\",\"paddingTop\":\"medium\",\"paddingBottom\":\"medium\",\"uiColor\":\"black\"}},\"346\":{\"type\":\"textWithImage\",\"enabled\":\"1\",\"fields\":{\"heading\":\"Going from A to B with one card.\",\"intro\":\"To highlight the wide range of ways of traveling e created a pattern set consisting of different routes, with different modes of transportation. A distinct look that sets it apart, but also tells a story.\",\"body\":\"\",\"image\":[\"345\"],\"textColor\":\"#000000\",\"backgroundColor\":\"#ffffff\",\"backgroundImage\":\"\",\"imagePosition\":\"left\",\"paddingTop\":\"medium\",\"paddingBottom\":\"none\",\"uiColor\":\"black\"}},\"490\":{\"type\":\"imageFull\",\"enabled\":\"1\",\"fields\":{\"image\":[\"489\"],\"paddingTop\":\"none\",\"paddingBottom\":\"none\",\"uiColor\":\"black\"}},\"349\":{\"type\":\"textTwoColumns\",\"enabled\":\"1\",\"fields\":{\"heading\":\"Many ways of getting there\",\"intro\":\"A big selection of modes of travel is what Shuttel is all about, and we wanted to bring that across everything we create using our patterns.\",\"body\":\"\",\"textColor\":\"#000000\",\"backgroundColor\":\"#ffffff\",\"backgroundImage\":\"\",\"paddingTop\":\"none\",\"paddingBottom\":\"small\",\"uiColor\":\"black\"}},\"348\":{\"type\":\"largeImage\",\"enabled\":\"1\",\"fields\":{\"image\":[\"524\"],\"backgroundColor\":\"#ffffff\",\"backgroundImage\":\"\",\"paddingTop\":\"small\",\"paddingBottom\":\"medium\",\"uiColor\":\"black\"}},\"487\":{\"type\":\"imageFull\",\"enabled\":\"1\",\"fields\":{\"image\":[\"486\"],\"paddingTop\":\"small\",\"paddingBottom\":\"small\",\"uiColor\":\"black\"}},\"351\":{\"type\":\"largeImage\",\"enabled\":\"1\",\"fields\":{\"image\":[\"350\"],\"backgroundColor\":\"#ff3f52\",\"backgroundImage\":[\"352\"],\"paddingTop\":\"small\",\"paddingBottom\":\"medium\",\"uiColor\":\"white\"}},\"354\":{\"type\":\"textTwoColumns\",\"enabled\":\"1\",\"fields\":{\"heading\":\"From online to offline\",\"intro\":\"We created a wide range of deliverables including logotypes, font choices, visual style, site design, out door and packaging.\",\"body\":\"\",\"textColor\":\"#ffffff\",\"backgroundColor\":\"#ff2441\",\"backgroundImage\":\"\",\"paddingTop\":\"medium\",\"paddingBottom\":\"medium\",\"uiColor\":\"white\"}},\"355\":{\"type\":\"largeImage\",\"enabled\":\"1\",\"fields\":{\"image\":[\"353\"],\"backgroundColor\":\"#ff2441\",\"backgroundImage\":\"\",\"paddingTop\":\"medium\",\"paddingBottom\":\"medium\",\"uiColor\":\"white\"}},\"432\":{\"type\":\"largeImage\",\"enabled\":\"1\",\"fields\":{\"image\":[\"483\"],\"backgroundColor\":\"#ff3f52\",\"backgroundImage\":[\"482\"],\"paddingTop\":\"medium\",\"paddingBottom\":\"medium\",\"uiColor\":\"white\"}},\"359\":{\"type\":\"imageGallery\",\"enabled\":\"1\",\"fields\":{\"images\":[\"357\",\"410\",\"412\",\"409\",\"411\"],\"backgroundColor\":\"#f5f5f5\",\"backgroundImage\":\"\",\"paddingTop\":\"medium\",\"paddingBottom\":\"medium\",\"uiColor\":\"black\"}}},\"41\":\"Shuttel\",\"31\":\"\",\"40\":[\"336\"],\"75\":\"\",\"76\":\"\",\"50\":\"\",\"39\":\"Branding the next mobility partner\",\"36\":\"\",\"83\":[\"337\"],\"43\":\"Identity\",\"34\":{\"339\":{\"type\":\"role\",\"enabled\":\"1\",\"fields\":{\"role\":\"Visual Design\"}},\"340\":{\"type\":\"role\",\"enabled\":\"1\",\"fields\":{\"role\":\"Branding\"}},\"341\":{\"type\":\"role\",\"ena